mapper.select:
select
    ta.*,
    tu.id ownerId, tu.name ownerName
from t_activity ta left join t_user tu on ta.owner_id = tu.id


    and ta.owner_id = #{ownerId, jdbcType=INTEGER}


    and ta.name like concat ('%', #{name, jdbcType=VARCHAR}, '%')


    and ta.start_time >= #{startDate, jdbcType=TIMESTAMP}


      and ta.end_dtime <= #{endDate, jdbcType=TIMESTAMP}   


    and ta.cost >= #{cost, jdbcType=DECIMAL}


    and ta.create_time >= #{createTime, jdbcType=TIMESTAMP}

mapper.delete:
delete from t_activity
where id in

  #{id, jdbcType=INTEGER}
